CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_transaksi`  AS SELECT 'A' AS `tbl`, `transaksi_kas`.`id` AS `id`,`transaksi_kas`.`tanggal_catat` AS `tgl`,if(`transaksi_kas`.`dk` = 'K',`transaksi_kas`.`jumlah`,if(`transaksi_kas`.`dk` is null,`transaksi_kas`.`jumlah`,0)) AS `kredit`,if(`transaksi_kas`.`dk` = 'D',`transaksi_kas`.`jumlah`,if(`transaksi_kas`.`dk` is null,`transaksi_kas`.`jumlah`,0)) AS `debet`,`transaksi_kas`.`dari_jenis_kas_id` AS `dari_kas`,`transaksi_kas`.`untuk_jenis_kas_id` AS `untuk_kas`,`transaksi_kas`.`jenis_transaksi` AS `transaksi` from `transaksi_kas` order by `tgl`  ;